package boj;

import java.util.Scanner;

public class ASCII11654 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		String s = sc.next();
		sc.close();
		char c = s.charAt(0);
		int answer = c+0;
		System.out.println(answer);
	}

}
